mapper.select:
select id, out_bill_code,location_code, only_code, parts_code, goods_code, goods_name, model, measure_unit, charg, supplier_code, supplier_name, tray_code, out_bill_num, out_bill_status, out_bill_time, del_flag, create_by, create_time, update_by, update_time, remark from wms_outbill_goods
${ew.sqlSegment}
order by create_time desc,location_code asc

select t.out_bill_code,location_code, t.goods_code, t.goods_name, t.model, t.measure_unit, t.charg, t.tray_code, sum(t.out_bill_num) out_bill_num, sum(t.num) num, t.out_bill_time from (select out_bill_code,location_code, goods_code, goods_name, model, measure_unit, charg, tray_code, out_bill_num, IF(out_bill_status=2,1,0) num, out_bill_time from wms_outbill_goods
${ew.sqlSegment}
order by create_time desc) t group by t.charg,t.tray_code,t.goods_code
