mapper.select:
select parts_code from wms_warehouse_tblstock
where del_flag = '0' and tray_code = #{trayCode}
order by SUBSTR(only_code,5,LENGTH(only_code))

select ifnull(count(*), 0) as num from wms_warehouse_tblstock where date(create_time) <
(select date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract(day from now()) day),interval -1 month) as date
) and del_flag = 0

select ifnull(count(*), 0) as num from wms_warehouse_tblstock where date(create_time) <
(select date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract(day from now())-1 day),interval 1 month) as date
) and del_flag = 0

select ifnull(count(*), 0) as num from wms_warehouse_tblstock where date(create_time) <
(select date_sub(date_sub(date_format(now(),'%y-%m-%d'),interval extract(day from now()) day),interval 0 month) as date
) and del_flag = 0
