mapper.select:
SELECT a.id, a.gate_way_device_no, a.remark, a.device_name as initDeviceName, sum( CASE WHEN a.device_type = 3 THEN 1 ELSE 0 END ) AS templateureNum, sum( CASE WHEN a.device_type = 4 THEN 1 ELSE 0 END ) AS smokeNum, sum( CASE WHEN a.device_type = 5 THEN 1 ELSE 0 END ) AS lightNum, sum( CASE WHEN a.device_type = 6 THEN 1 ELSE 0 END ) AS ammeterNum, sum( CASE WHEN a.device_type = 7 THEN 1 ELSE 0 END ) AS freshNum, sum( CASE WHEN a.device_type = 10 THEN 1 ELSE 0 END ) AS dehumidifierNum FROM ( SELECT wgwr.id, wgwr.gate_way_device_no, wgwr.remark, wgwr.no_ip_device_no, wdbi.device_type, wdb.device_name FROM wcs_gate_way_realtion wgwr LEFT JOIN wcs_device_base_info wdbi ON wgwr.no_ip_device_no = wdbi.device_no LEFT JOIN wcs_device_base_info wdb ON wgwr.gate_way_device_no = wdb.device_no
${ew.sqlSegment}
) a GROUP BY a.gate_way_device_no
