SELECT a.id, a.meter_device_no, a.remark, a.device_name as initDeviceName, sum( CASE WHEN a.device_type = 2 THEN 1 ELSE 0 END ) AS stackerNum, sum( CASE WHEN a.device_type = 5 THEN 1 ELSE 0 END ) AS lightNum, sum( CASE WHEN a.device_type = 7 THEN 1 ELSE 0 END ) AS freshNum FROM ( SELECT wgwr.id, wgwr.meter_device_no, wgwr.remark, wgwr.device_no, wdb.device_type, wdbi.device_name FROM wcs_meter_device_realtion wgwr LEFT JOIN wcs_device_base_info wdbi ON wgwr.meter_device_no = wdbi.device_no LEFT JOIN wcs_device_base_info wdb ON wgwr.device_no = wdb.device_no
${ew.sqlSegment}
) a GROUP BY a.meter_device_no
